mapper.select:
select SCHE_NO as sche_no ,SCHE_DT as sche_dt ,SCHE_TITLE as sche_title ,SCHE_CONTENT as sche_content ,SCHE_SE as sche_se ,REGIST_ID as regist_id ,REGIST_DT as regist_dt from SCHE_LIST where SCHE_TITLE like '%' || #{schTitle} || '%' and SCHE_CONTENT like '%' || #{schContent} || '%'
and SCHE_DT >= #{schDtFrom}
and SCHE_DT <= #{schDtTo}
order by SCHE_DT, SCHE_NO

mapper.update:
    	update SCHE_LIST set
			SCHE_DT = #{schDtFrom}
			,SCHE_TITLE = #{schTitle}
			,SCHE_CONTENT = #{schContent}
			,SCHE_SE = #{schSe}
			,REGIST_ID = #{registId}
			,REGIST_DT = sysdate
		where SCHE_NO = #{schId}

mapper.select:
	select
		count(*) as cnt 
	from SCH_DUTY_LIST 
	where DUTY_MONTH = #{month} 
	  and DUTY_YEAR = #{year}
    order by SORT_ORDER asc
